package Geekforgeeks.geekforgeeks;
import java.util.Scanner;

public class whileLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
 Scanner sc=new Scanner(System.in);
  int n=sc.nextInt();
  int i=0;
  while(i<=n) {
	  System.out.print(""+n+" ");
	  n--;

//this is for test
  }

	}

}
